		/**
		 * Data
		 *
		 * Get the installation data for a given plugin.
		 */

		public function data( $plugin ) {

			if( $plugin['type'] === 'internal' ){

				/*
				 * If the plugin is from the Wordpress repository.
				 */

				// Attempt pulling the data from the cache.
				$data = get_transient( 'ascripta_installer_data_' . $plugin['slug'] );

				// If no cache could be found, build the plugin array.
				if( false === $data ){

					if ( ! function_exists( 'plugins_api' ) ) {
						require_once ABSPATH . 'wp-admin/includes/plugin-install.php';
					}

					// Connect to the Wordpress API. 
					$data = plugins_api( 'plugin_information', array(
						'slug' => $plugin['slug'],
						'fields' => array(
							'short_description' => true,
							'description'       => false,
							'sections'          => false,
							'tested'            => false,
							'requires'          => false,
							'rating'            => false,
							'ratings'           => false,
							'downloaded'        => false,
							'downloadlink'      => false,
							'last_updated'      => false,
							'added'             => false,
							'tags'              => false,
							'compatibility'     => false,
							'homepage'          => false,
							'versions'          => false,
							'donate_link'       => false,
							'reviews'           => false,
							'banners'           => false,
							'icons'             => false,
							'active_installs'   => false,
							'group'             => false,
							'contributors'      => false
						),
					) );

					if ( is_wp_error( $data ) ) {
						wp_die( $data );
					}

					// Cache the results.
					set_transient( 'ascripta_installer_data_' . $plugin['slug'], $data, 60 * 24 * 60 * 7 );

				} 
				
				// Convert the response to an array.
				$plugin = (array)$data;

				// If we have plugin data.
				if( $plugin['slug'] ){

					// Create the thumbnail src.
					$plugin['thumbnail'] = 'https://ps.w.org/' . $plugin['slug'] . '/assets/icon-256x256.png';
					if ( !@getimagesize( $plugin['thumbnail'] ) ) {
						$plugin['thumbnail'] = 'http://placehold.it/256x256?text=&nbsp;';
					}

					// Generate the link for more plugin details.
					$plugin['details'] = 'https://wordpress.org/plugins/' . $plugin['slug'] . '/';

					// Generate the installation URL.
					$plugin['url'] = wp_nonce_url(
						add_query_arg(
							array(
								'action' => 'install-plugin',
								'plugin' => $plugin['slug']
							),
							admin_url( 'update.php' )
						),
						'install-plugin_' . $plugin['slug']
					);

				}

			} else {

				/*
				 * If the plugin is an external one.
				 */

				// Extract the plugin details.
				$plugin = array(
					'name'              => $plugin['name'], 
					'slug'              => $plugin['slug'],
					'index'             => isset( $plugin['index'] ) ? $plugin['index'] : null,
					'version'           => $plugin['version'],
					'type'              => $plugin['type'],
					'details'           => $plugin['author_uri'],
					'url'               => $plugin['url'],
					'thumbnail'         => $plugin['thumbnail'],
					'short_description' => $plugin['short_description'],
					'author'            => '<a href="' . $plugin['author_uri'] . '" target="_blank">' . $plugin['author'] . '</a>'
				);

			}

			// Return the updated plugin variable.
			return isset( $plugin ) ? $plugin : null;

		}


		/**
		 * Plugin
		 *
		 * Output the individual plugin markup.
		 */

		public function plugin( $plugin ) {

			// Store the slug depending of the plugin type.
			$index = isset( $plugin['index'] ) ? $plugin['index'] : $plugin['slug'];
			
			// Only show the plugin if it's not already installed or activated.
			if( !$this->is_installed_plugin( $plugin['slug'] . '/' . $index . '.php' ) ) {
				
				// Get the repository data.
				if( $plugin = $this->data( $plugin ) ){ ?>

					<div class="plugin-card plugin-card-<?php echo $plugin['slug']; ?>">

						<div class="plugin-card-top">

							<!-- Name -->
							<?php $this->plugin_name( $plugin ); ?>
							
							<!-- Links -->
							<?php $this->plugin_links( $plugin ); ?>

							<!-- Description -->
							<?php $this->plugin_description( $plugin ); ?>

						</div>

						<div class="plugin-card-bottom">
							
							<div class="column-downloaded">
								<strong><?php esc_html_e( 'Version:', 'ascripta' ); ?></strong>
								<?php echo $plugin['version']; ?>
							</div>

						</div>

					</div>

				<?php }

			}

		}


